package com.basics;

public class PyramiInvers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 for(int i=1;i<=5;i++)
	        {
	        	for(int j=1;j<=i;j++)
	        	{
	        		System.out.print("*");
	        	}
	        	System.out.println();
	        }
		 for(int k=4;k>=0;k--)
	        {
	        	for(int l=1;l<=k;l++)
	        	{
	        		System.out.print("*");
	        	}
	        	System.out.println();
	        }
	}

}
