package com.basics;

public class SqrCub {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
             double no=5;
             double square=no*no;
             double cube=no*no*no;
             double fourth=no*no*no*no;
             System.out.println("Number= "+no+" Square "+square+" Cube "+cube+" Fourth power"+fourth);
	}

}
